import React, { useState, Component } from "react";

import Movie from "./movie_element.js";
import GridList from "@material-ui/core/GridList";
import Modal from "@material-ui/core/Modal";
import { ButtonDropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';
import Dropdown from './Dropdown';

function rootStyle() {
  return {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "space-around",
    overflow: "hidden",
  };
}
function GridListStyle() {
  return {
    width: "95%",
    height: 650,
    position: "fixed",
    // Promote the list into his own layer on Chrome. This cost memory but helps keeping high FPS.
    transform: "translateZ(0)",
  };
}
function info() {
  return {
    width: "auto",
    height: "auto",
    justifyContent: "center",
    textAlign: "center",
    paddingTop: "23%",
    paddingLeft: "10px",
    paddingRight: "10px",
    lineHeight: "200%",
    backgroundColor: "#c3cfe2",
  };
}
function getModalStyle() {
  return {
    top: "20%",
    left: "20%",
    backgroundColor: "transparent",
    position: "absolute",
    display: "flex",
  };
}


const movies_list = [
  {
    key: 0,
    id: "tt7605074",
  },
  {
    key: 1,
    id: "tt0816692",
  },
  {
    key: 2,
    id: "tt1457767",
  },
  {
    key: 3,
    id: "tt0499549",
  },
  {
    key: 4,
    id: "tt4154796",
  },
  {
    key: 5,
    id: "tt5700672",
  },
  {
    key: 6,
    id: "tt10039344",
  },
  {
    key: 7,
    id: "tt0198781",
  },
];


const lists = [
  {
    id: 1,
    value: 'All',
  },
  {
    id: 2,
    value: 'Watched',
  },
  {
    id: 3,
    value: 'WannaWatch',
  },
];



export default class MovieGallery extends Component {
  constructor() {
    super();
    this.state = {
      moviePoster: "",
      open: false,
      key: 1,
      isOpen: false,
      toggle: 0
    };
    this.changeMoviePoster = (data) => {
      this.setState({
        moviePoster: data,
      });
    };
    this.changeKey = (k) => {
      this.setState({
        key: k,
      });
    };
    this.setOpen = (o) => {
      this.setState({
        open: o,
      });
    };
  }




  render() {
    return (
      <div style={{display:"block"}}>
        <div>
          <DropdownMenu items={lists}/>
        </div>


      </div>
    );
  }
}
