import React, { useState, Component } from "react";

import Movie from "./movie_element.js";
import GridList from "@material-ui/core/GridList";
import Modal from "@material-ui/core/Modal";
import IconButton from "@material-ui/core/IconButton";
import DeleteSweepIcon from '@material-ui/icons/DeleteSweep';
import { ButtonDropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';
import Dropdown from './Dropdown';

function rootStyle() {
  return {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "space-around",
    overflow: "hidden",
  };
}
function GridListStyle() {
  return {
    width: "95%",
    height: "85vh",
    //position: "fixed",
    // Promote the list into his own layer on Chrome. This cost memory but helps keeping high FPS.
    //transform: "translateZ(0)",
  };
}
function info() {
  return {
    width: "auto",
    height: "auto",
    justifyContent: "center",
    textAlign: "center",
    paddingTop: "23%",
    paddingLeft: "10px",
    paddingRight: "10px",
    lineHeight: "200%",
    backgroundColor: "#c3cfe2",
  };
}
function getModalStyle() {
  return {
    top: "20%",
    left: "20%",
    backgroundColor: "transparent",
    position: "absolute",
    display: "flex",
  };
}

const selectList = [
  { value: 'All', name: 'All' },
  { value: 'WannaWatch', name: 'WannaWatch' },
  { value: 'Watched', name: 'Watched'}        
];





export default class MovieGallery extends Component {
  constructor() {
        super();
        this.state = {
          moviePoster: "",
          open: false,
          key: 1,
          isOpen: false,
          toggle: 0,
          visible: 8,
          movies_list: [
            {key: 0,  id: "tt7605074",},
            {key: 1,  id: "tt0816692",},
            {key: 2,  id: "tt1457767",},
            {key: 3,  id: "tt0499549",},
            {key: 4,  id: "tt4154796",},
            {key: 5,  id: "tt5700672",},
            {key: 6,  id: "tt10039344",},
            {key: 7,  id: "tt0198781",},
            {key: 8,  id: "tt5013056",},
            {key: 9,  id: "tt0027977",},
            {key: 10, id: "tt2543164",},
            {key: 11, id: "tt0448115",},
            {key: 12, id: "tt1049413",},
            {key: 13, id: "tt2250912",},
            {key: 14, id: "tt2948356",},
            {key: 15, id: "tt1454468",},
          ]


        };
        this.changeMoviePoster = (data) => {
          this.setState({
            moviePoster: data,
          });
        };
        this.changeKey = (k) => {
          this.setState({
            key: k,
          });
        };
        this.setOpen = (o) => {
          this.setState({
            open: o,
          });
        };

        this.loadmore = this.loadmore.bind(this);  //bind evnt handler! 
  }

  //load more button 
  loadmore(){         //binded in constructor
    this.setState((old)=>{
      return {visible: old.visible + 8}  //trys to set visible 8 more movies   
    })
  }


  render() {
    return (
      <div style={{display:"block"}}>
        <div>
          {/*Dropdown menu*/}
          <select name="selectList" >
            {selectList.map((e, key) => {
              return <option key={key} value={e.value}>{e.name}</option>;
            })}
          </select>
        </div>




        <div>
          <GridList cellHeight={150} spacing={1} style={GridListStyle()}>
            {this.state.movies_list.slice(0,this.state.visible).map((movie) => (
              <Movie
                movieid={movie.id}
                indexKey={movie.key}
