/*
used for error detection in customized hook useForm.js
*/
export default function form_validate(values){    //takes in "values" to check correctness
    let errors = {};    //along the checking process, will add errors on any objects 
    errors.hasError = "false";
    errors.guestName="";
    errors.message="";
    errors.description="";

    if(!values.guestName){
        errors.guestName = "Your name is required."; //adds the key
        errors.hasError = "true";
    }else if(values.guestName.length < 5  ){
        errors.guestName = "Name should be at least 5 characters"
        errors.hasError = "true";
    }else if(values.guestName.length > 20){
        errors.guestName = "Name should not contain more than 20 characters"
        errors.hasError = "true";
    }

    if(!values.message){
        errors.message = "Your message is required."; //adds the key
        errors.hasError = "true";
    } else if(values.message.length < 15 || values.message.length > 500){
        errors.message = "Enter more than 15, less than 500"
        errors.hasError = "true";
    }

    if(values.description.length >= 100){
        errors.description = "Write description less then 100 characters"
        errors.hasError = "true";
    }

    return errors;
}           
